import base64
import json

# Base64 completo extraído do HTML original
base64_data = ''

# Decodifica o base64
decoded = base64.b64decode(base64_data).decode('utf-8')
data = json.loads(decoded)

# Salva em arquivo JSON
with open('public/infracoes-complete.json', 'w', encoding='utf-8') as f:
    json.dump(data, f, ensure_ascii=False, indent=2)

print(f"✓ Extraídas {len(data)} categorias de infrações")
total_infracoes = sum(len(cat.get('infracoes', [])) for cat in data)
print(f"✓ Total de {total_infracoes} infrações individuais")
print(f"✓ Arquivo salvo em: public/infracoes-complete.json")
